import React, {ChangeEvent, useState, ChangeEventHandler} from 'react';

export function CreateProduct() {
const [value, setValue] = useState('');
console.log(value);
const submitHandler = (event: React.FormEvent) => {
  event.preventDefault()
}

const changeHandler : ChangeEventHandler = (event: R<HTMLInputElement>) => {
  const target = (event.target as HTMLInputElement)
  setValue(target.value)


}

  return (
    <form onSubmit={submitHandler}>
      <input
      type = 'text'
      className='border py-2 px-4 mb-2 w-full outline-0'
      placeholder='Enter product title...'
      value={value}
      onChange = {() => changeHandler}
      />
      <button type='submit' className='py-2 px-2 border bg-yellow-400 hover:text-white'>Create</button>
    </form>
  )
}
